import React, { useState } from "react";
import Card from "../Components/Card";
import Wdata from "../Components/WatchData";

function wcard(val) {
  return <Card imgsrc={val.imgsrc} title={val.title} price={val.price} />;
}

const Products = () => {
  const [items, setItems] = useState(Wdata);
  const filterwatch = (watchs) => {
    const updateditem = Wdata.filter((Data) => {
      return Data.gender === watchs;
    });
  };
  return (
    <>
      <div className="container">
        <div className="row my-4 py-5">
          <div className="col-md-6 text-center">
            <h6>SORT BY</h6>
          </div>
          <div className="col-md-6">
            <button
              className="btn btn-light mx-3"
              onClick={() => filterwatch(men)}
            >
              {" "}
              Men{" "}
            </button>
            <button
              className="btn btn-light"
              onClick={() => filterwatch(women)}
            >
              {" "}
              Woman{" "}
            </button>
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row">{Wdata.map(wcard)}</div>
      </div>
    </>
  );
};

export default Products;
